public class ContenidoBeanEdit {

	/** The nombre. */
	private String nombre;

	/** The id contenido. */
	private String idContenido;

	/** The regitrado. */
	private boolean regitrado;

	/**
	 * Obtiene nombre. Fecha: 18/02/2013
	 * 
	 * @return nombre
	 */
	public String getNombre() {
		return nombre;
	}

	/**
	 * Establece el nombre.
	 * 
	 * @param nombre el new nombre
	 */
	public void setNombre(String nombre) {
		this.nombre = nombre;
	}

	/**
	 * Obtiene id contenido. Fecha: 18/02/2013
	 * 
	 * @return id contenido
	 */
	public String getIdContenido() {
		return idContenido;
	}

	/**
	 * Establece el id contenido.
	 * 
	 * @param idContenido el new id contenido
	 */
	public void setIdContenido(String idContenido) {
		this.idContenido = idContenido;
	}

	/**
	 * Comprueba si es regitrado. Fecha: 18/02/2013
	 * 
	 * @return true, si es regitrado
	 */
	public boolean isRegitrado() {
		return regitrado;
	}

	/**
	 * Establece el regitrado.
	 * 
	 * @param regitrado el new regitrado
	 */
	public void setRegitrado(boolean regitrado) {
		this.regitrado = regitrado;
	}
}
